import React from 'react';
import { motion } from 'framer-motion';
import styled from '@emotion/styled';

const Container = styled(motion.div)`
  max-width: 1200px;
  margin: 0 auto;
  padding: 2rem;
  background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
  min-height: 100vh;
`;

const LayoutContainer = styled.div`
  position: relative;
  min-height: 100vh;
  padding: 20px;
  background: white;

  &::before {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    width: 15px;
    height: 100%;
    background: linear-gradient(90deg, rgba(76, 175, 80, 0.3) 0%, rgba(255, 255, 255, 0) 100%);
    box-shadow: 0 0 20px rgba(76, 175, 80, 0.2);
    z-index: 1000;
  }
`;

const Layout = ({ children }) => {
  return (
    <LayoutContainer>
      <Container
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.5 }}
      >
        {children}
      </Container>
    </LayoutContainer>
  );
};

export default Layout;
